
public class GreatestofThreeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=50;
    int b=25;
    int c=70;
    if(a>b&&a>c)
    {
    	System.out.println("a is greater");
    }
    else if(b>c&&b>a)
    {
    	System.out.println("B is greater");
    	
    }
    else
    {
    	System.out.println("c is greater");
    }
	}

}
